/**
 * Offcanvas
 **/
$(function() {
	$('#js-bootstrap-offcanvas').prepend('<i class="fa fa-bars offcanvas-toggle" aria-hidden="true" data-toggle="offcanvas" data-target="#js-bootstrap-offcanvas"></i>');
	$('.offcanvas-toggle').on('click', function(e) {
		e.preventDefault();
		toggleNav();
	});	
	$('#js-bootstrap-offcanvas .nav-link').on('click', function() {
		// If nav-link has href, toggle (close) nav (could be anchor link on same page)
		if(typeof $(this).attr('href') !== 'undefined')
			toggleNav();
	});
});

function toggleNav() {
	$joc = $("#js-bootstrap-offcanvas");
	$jtoggle = $('.offcanvas-toggle');
	if($joc.hasClass('in')) {
		$joc.removeClass('in');
		$jtoggle.removeClass('is-open');
	} else {
		$joc.addClass('in');
		$jtoggle.addClass('is-open');
	}
}

var NAV_COLLAPSER_OPTS = {
	collapser: '.nav .collapser',
	navLink: '.nav .nav-link',
	subNav: '> ul',
	expandedClass: 'open',
	hiddenClass: 'hidden'
};

/**
 * Nav Collapser
 **/
$(function(opts) {
	$(opts.collapser).click(function(e) {
		e.preventDefault();
		toggle($(this).parent(), opts);
		return false;
	});
	$(opts.navLink).click(function(e) {
		if(typeof $(this).attr('href') === 'undefined') {
			e.preventDefault();
			toggle($(this).parent(), opts);
			return false;
		}
	});
	function toggle($jni, opts) {
		if($jni.hasClass(opts.expandedClass)) {
			$jni.attr('aria-expanded', 'false').removeClass(opts.expandedClass);
			$jni.find(opts.subNav).slideUp();
		} else {
			$jni.attr('aria-expanded', 'true').addClass(opts.expandedClass);
			$jni.find(opts.subNav).hide().removeClass(opts.hiddenClass).slideDown();
		}
	}
}(NAV_COLLAPSER_OPTS));


/**
 * Initial scroll if marker
 */
jQuery(window).on('load', function() {
	var href = window.location.href;
	if(href.indexOf('#') > 0) {
		var hash = href.substring(href.indexOf('#'));
		
		if(jQuery(hash).length) {
			window.setTimeout(function() {

				// Accordion collapse scroll to if hidden
				var jaL = jQuery(".accordion .card a[href='"+hash+"']");
				if(jaL.length)
					jaL.click();

				osScrollTo(jQuery(hash).offset().top);
			}, 500);
			

		}
	}
});

jQuery(document).ready(function() {
	
	// Panel collapse scroll to if hidden
	jQuery(".collapse").on('shown.bs.collapse', function(){
		var ot = jQuery(this).offset().top,
			wt = jQuery(window).scrollTop();
			
			if(ot < wt)
				osScrollTo(jQuery(this).parent().offset().top);
	});
});

function isUtilityLink($element) {
	var uLinks = ['popup', 'carousel-control', 'carousel-control-prev', 'carousel-control-next'];
	
	// Check all normal utitility links
	for(var i in uLinks)
		if($element.hasClass(uLinks[i]))
			return true;
		
	// If is accordion / panel collapse / data-toggle
	if($element.hasClass('btn-accordion') || typeof $element.attr('data-toggle') != 'undefined')
		return true;
	
	// If is slidedown
	if($element.hasClass('slidedown') || $element.closest('.slidedown').length > 0)
		return true;
	
	return false;
}

/**
 * Anchor link slider 
 **/
function anchorLinkListener(e) {
	// If link is utility link / has another functionality
	if(isUtilityLink($(this)))
		return;
	
	// If no href anchor present, do nothing
	if(typeof $(this).attr('href') === 'undefined')
		return;
			
	var href = jQuery(this).attr('href'),
		hash = href.substring(href.indexOf('#'));
		
	// If external Url do nothing
	if(href.indexOf('http') >= 0 && href.indexOf(location.host) < 0)
		return;
		
	// If anchor is empty, do nothing
	if(hash === '#')
		return;
	
	var jE = jQuery(hash);	
	if(jE.length) {
		e.preventDefault();
		
		// Open panel if necessary
		if(jE.hasClass('panel') && !jE.find('.panel-collapse').hasClass('in')) {
			jE.find('.panel-heading .btn').click();	//jE.collapse('show');
		}
		// Open accordion if necessary
		if(jE.closest('.collapse').length && !jE.closest('.collapse').hasClass('show')) {
			jE = jE.closest('.collapse').parent();
			jE.find('[data-toggle="collapse"]').click();
		}
		
		// Slide to element
		var top = jE.offset().top;
		osScrollTo(top);
	}
}

/** Anchor link slider **/
jQuery('body').on('click', 'a[href*="#"]', anchorLinkListener);


