/**
 * Affix elements to top. 
 * 
 * 25.09.2020
 * @author: Kai Vier
 **/
 
function initFix(options) {
	// Save already fixed elements, to calculate spaces
	var $pesDone = [];
	
	// Collect page-elements as jQuery objs
	var $pes = [];
	for(var i in options.elements) {
		$pes.push($(options.selectorBefore + options.elements[i] + options.selectorAfter));
	}
		
	// Set fixed-classes and do calculations
	for(var i in $pes) {
		$pes[i].addClass(options.className);
		var keep = options.classKeep ? $pes[i].hasClass(options.classKeep) : false;
		// Affix element
		updateFix($pes[i], $pesDone, keep);
		
		// Update sizes/offsets on resize
		$(window).on('resize', () => {
			updateFix($pes[i], $pesDone, keep);
		});
		// Set element to pesDone
		$pesDone.push($pes[i]);
	}
}


var modalTemplate = '<div class="modal fade" id="myModal" tabindex="-1" role="dialog" aria-labelledby="myModalLabel" aria-hidden="true"><div class="modal-dialog"><div class="modal-content">'
+ '<div class="modal-header"><button type="button" class="close" data-dismiss="modal" aria-label="Schlieen"><span aria-hidden="true">&times;</span></button><h3 class="modal-title">Modal Title</h3></div>'
+ '<div class="modal-body"></div>'
+ '<div class="modal-footer"><button type="button" class="btn btn-default" data-dismiss="modal">Schlieen</button></div>'
+ '</div></div></div>';

function initPopupContent(jpc) {
	var jM = jQuery(modalTemplate).clone(true,true);
	
	if(jpc.attr('id') == "")
		return;
	
	jM.attr('id', 'popup-'+jpc.attr('id'));
	
	var jTitle = jpc.find('> h1, > h2, > h3, > p');
	jM.find('.modal-title').html(jTitle.html());
	var jBody = jpc.find('.ce-bodytext').html();
	jM.find('.modal-body').html(jBody);
	
	jQuery('body').append(jM);
}

/**
 * Initialize Auto Popup
 *
 * lib = fancybox | magnific | ...
 */
function initPopupAuto(lib) {
	console.log("Cookie Popup: Init ("+lib+")");	
	
	if(typeof lib == "undefined" || lib == "") {
		console.error("Cookie Popup: Popup library not defined!");
		return;
	}
	
	jQuery('.popup-content.popup-auto').each(function() {

		var id = jQuery(this).attr('id');
		if(typeof id != "undefined" && id != "") {
			// return, if the cookie of this popup is already set (popup should not be displayed)
			var dataCookie = jQuery(this).attr('data-popup-cookie');
			if(typeof dataCookie != "undefined" && dataCookie != "" && ipGetCookie(dataCookie))
				return;

			console.log("Cookie Popup: Open ("+jQuery(this).attr('id')+")");
			
			// Create popup
			if(lib == 'fancybox')
				createPopupFancybox(id, jQuery(this));
			else if(lib == 'magnific')
				createPopupMagnific(id, jQuery(this));
			else if(lib == 'simplemodal')
				createPopupSimpleModal(id, jQuery(this));
			else if(lib == 'bootstrapmodal')
				createPopupBootstrapModal(id, jQuery(this));
			
			// Set cookie
			if(typeof dataCookie != "undefined" && dataCookie != "")
				ipSetCookie(dataCookie,1);
		}
	});
}
function createPopupFancybox(id, jThis) {
	var dataStyle = jThis.attr('data-popup-style');
	var baseClass = (typeof dataStyle != "undefined" && dataStyle != "") ? dataStyle : '';

	jQuery.fancybox.open({
		src  : '#'+id,
		type : 'inline',
		baseClass : baseClass
	});
}
function createPopupMagnific(id, jThis) {
	$.magnificPopup.open({
		items: {
			src: '#'+id 
		},
		type: 'inline'
	}, 0);
}
function createPopupSimpleModal(id, jThis) {
	$("#"+id).modal();	
}
function createPopupBootstrapModal(id, jThis) {
	var jMd = jQuery('#popup-'+id).length ? jQuery('#popup-'+id) : jQuery('#'+id);
	if(jMd.length) {
		jMd.modal('show');
	}
}
function ipSetCookie(name,value,days) {
    var expires = "";
    if (days) {
        var date = new Date();
        date.setTime(date.getTime() + (days*24*60*60*1000));
        expires = "; expires=" + date.toUTCString();
    }
    document.cookie = name + "=" + (value || "")  + expires + "; path=/";
}
function ipGetCookie(name) {
    var nameEQ = name + "=";
    var ca = document.cookie.split(';');
    for(var i=0;i < ca.length;i++) {
        var c = ca[i];
        while (c.charAt(0)==' ') c = c.substring(1,c.length);
        if (c.indexOf(nameEQ) == 0) return c.substring(nameEQ.length,c.length);
    }
    return null;
}
