/**
 * Transition effect for navbar 
 * Options can be set in Typo3 HTML Element like:
	<script>
	var parallaxToggles = [{
		scrollListenerSelector: window, 
		elementSelector: '.page-header > div',
		topOffset: 500,
		className: 'solid'
	}];
	</script>
 */
jQuery(document).ready(() => {
	if(typeof parallaxToggles != 'undefined' && parallaxToggles) {
		for(var pt in parallaxToggles) {
			var ptOpts = parallaxToggles[pt];
			initParallaxToggle(
				$(ptOpts.scrollListenerSelector), 
				$(ptOpts.elementSelector), 
				ptOpts.topOffset, 
				ptOpts.className
			);
		}
	}
	//	initParallaxToggle($(window), $('.page-header > div'), 500, 'solid');
});
